import React from 'react';
import Hog from './Hog';

class HogBrowser extends React.Component {
    showInfo = (hog) => {

        // if (event.target.children[1].className === 'hidden'){
        //     event.target.children[1].className = ''
        //    } else {
        //        event.target.children[1].className = 'hidden'
        //    }
    }
    render(){
        const {piggies} = this.props

        return(
            <div className="ui grid container">
                {piggies.map(piglet => 
                    <Hog piglet={piglet} showInfo={this.showInfo}/>
                )}
            </div>
        )
    }
}

export default HogBrowser;
