import React from 'react'; 

state = {
    selectedDisplay: {
        "Mudblood": 'hog',
        "Porkchop": 'hog',
        "Cherub": 'hog',
        "Piggy smalls": 'hog',
        "Trouble": 'hog',
        "Sobriety": 'hog',
        "Rainbowdash": 'hog',
        "TruffleShuffle": 'hog',
        "Bay of Pigs": 'hog',
        "The Prosciutto Concern": 'hog',
        "Galaxy Note": 'hog', 
        "Leggo My Eggo": 'hog',
        "Augustus Gloop": 'hog'
    }
}

class Hog extends React.Component{
    showHogInfo = (piglet) => {
        this.state.selectedDisplay[piglet.name] = this.state.selectedDisplay[piglet.name] === 'hog' ? 'hogInfo' : 'hog'
        this.setState({}); 
        console.log(piglet)
    }

    render(){
        const {piglet, mugshots, showHog} = this.props; 
        return(
            <div 
            className='minPigTile' 
            style={{backgroundImage:`url(${mugshots[piglet.name]})`}}
            onClick={() => showHog(piglet)}
            >
                <h3> {piglet.name} </h3>
            </div>
        )
    }
}

export default Hog; 
const hogDisplay = (piglet) => {
    return(
        <li className=' pigTile' >
            {this.state.selectedDisplay[piglet.name] === 'hog' && 
            <Hog piglet={piglet} mugshots={mugshots} showHog={this.showHogInfo}/>}

            {this.state.selectedDisplay[piglet.name] === 'hogInfo' && 
            <HogInfo piglet={piglet} mugshots={mugshots}/>}   
        </li>                 
    )     
}
